import { Component,Input } from '@angular/core';
import {MatDialog} from '@angular/material/dialog';
import {  FormGroup, Validators, FormArray, FormControl, FormBuilder } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { DataService } from 'src/app/allService/data.service';
import Swal from 'sweetalert2';
import { UserService } from 'src/app/allService/user.service';

@Component({
  selector: 'app-add-user',
  templateUrl: './add-user.component.html',
  styleUrls: ['./add-user.component.css']
})
export class AddUserComponent {
  @Input() dataSourse: any;
  user!: FormGroup;
  s_Group: any;
  constructor(public dialog: MatDialog,private route: ActivatedRoute, private fb: FormBuilder,private _userService :UserService) {
  }
  ngOnInit(){
    this.user = this.fb.group({
      userName:[["", Validators.required]],
      userId: ["", Validators.required],
      password: ["", Validators.required],
      confirmPassword: ["", Validators.required],
  });
  this._userService.getUser().subscribe((res:any)=>{
    this.s_Group=res.data
  })
  if(this.dataSourse){
    this.user.patchValue({
      sName:this.dataSourse.sName,
      sCharge:this.dataSourse.sCharge,

    })
  }
    }

    saveUser(){
   if(this.dataSourse){
    this._userService.updateUser(this.dataSourse.id,this.user.value).subscribe((res:any)=>{
      if(res){
        Swal.fire({
          icon: 'success',
          title: 'Service Update',
          showConfirmButton: false,
          timer: 1200
        });
        this.dialog.closeAll()
      }
    })
   }
   else{
    this._userService.saveUser(this.user.value).subscribe((res:any)=>{
      if(res){
        Swal.fire({
          icon: 'success',
          title: 'Service has been saved',
          showConfirmButton: false,
          timer: 1200
        });
        this.dialog.closeAll()
      }
    })
   }

  }
}


<section>
    <div class="m-3">
        <h4 class="modal-title">Add/Edit Service</h4>
    </div>
    <hr>
    <form [formGroup]="user" (ngSubmit)="saveUser()">
        <div class="w-11/12 p-2 m-2">
            <label for="exampleFormControlInput1" class="form-label">User Name</label>
            <input type="text" class="form-control" id="exampleFormControlInput1" formControlName="userName" >
        </div>
        <div class="w-11/12 p-2 m-2">
            <label for="exampleFormControlInput1" class="form-label">User Id</label>
            <input type="text" class="form-control" id="exampleFormControlInput1" formControlName="userId" >
        </div>
<div class="w-11/12 p-2 m-2">
    <label for="exampleFormControlInput1" class="form-label"> Password</label>
    <input type="password" class="form-control" id="exampleFormControlInput1" formControlName="password" >
</div>
<div class="w-11/12 p-2 m-2">
    <label for="exampleFormControlInput1" class="form-label">Confirm Password</label>
    <input type="password" class="form-control" id="exampleFormControlInput1" formControlName="confirmPassword" >
</div>

<div class=" w-100 gap-2 d-flex justify-content-end p-2">
    <button  mat-dialog-close class="btn btn-white"  >Close</button>
    <button type="submit" class="btn btn-success"  >Submit</button>
</div>

</form>
</section>
