import { Component, OnInit } from '@angular/core';
import {MatDialog} from '@angular/material/dialog';
import { NewRegistrationComponent } from '../new-registration/new-registration.component';
import { DataService } from 'src/app/allService/data.service';
import { NgxSpinnerService } from "ngx-spinner";
import { MatTableDataSource } from '@angular/material/table';
import Swal from 'sweetalert2';
import { PeriodicElement } from '../../admin/department/department.component';
@Component({
  selector: 'app-registration',
  templateUrl: './registration.component.html',
  styleUrls: ['./registration.component.css']
})
export class RegistrationComponent {
  constructor(public dialog: MatDialog,private spinner: NgxSpinnerService,private service:DataService) {
  }
  displayedColumns: string[] = ['id', 'name','Department','Specialization','GeneralVisit','Action'];
  listData!:PeriodicElement[];
  dataSource:any;
  ngOnInit(){
    this.spinner.show();
    setTimeout(() => {
      this.spinner.hide();
    }, 1000);

this.service.getConsultant().subscribe((res:any)=>{
  this.listData=res.data;
  console.log(res.data)
  this.dataSource=new MatTableDataSource(this.listData)
})
}
  openDialog(){
    const dialogRef = this.dialog.open(NewRegistrationComponent);
    dialogRef.afterClosed().subscribe(result => {
    this.ngOnInit()
    })

  }
  edit(name:any,id:any){
console.log(id)
console.log(name)

  }
  delete(name:any,id:any){
    Swal.fire({
      title: 'Are you sure?',
      text: "You won't be able to revert this!",
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Yes, delete it!'
    }).then((result) => {
      if (result.isConfirmed) {
        this.service.deleteDepartment(id).subscribe((res:any)=>{
          if(res.message){
            this.ngOnInit();
          }
        })
        Swal.fire(
          'Deleted!',
          'Your file has been deleted.',
          'success'
        )
      }
    })


  }


}
<button  (click)="openDialog()" class="btn btn-sm btn-primary">New Registration</button>
<!-- <ngx-spinner type="square-loader"></ngx-spinner> -->
<ngx-spinner bdColor = "rgba(0, 0, 0, .4)" size = "default" color = "black" type = "square-loader" [fullScreen] = "true"><p style="color: rgb(8, 1, 1)" >  </p></ngx-spinner>
<div class="m-4">
  <div class="table-heading">Opd Record List</div>
<table mat-table [dataSource]="dataSource" class="mat-elevation-z8">

  <!-- Position Column -->
  <ng-container matColumnDef="id">
    <th mat-header-cell *matHeaderCellDef> Id. </th>
    <td mat-cell *matCellDef="let element"> {{element.id}} </td>
  </ng-container>

  <!-- Name Column -->
  <ng-container matColumnDef="name">
    <th mat-header-cell *matHeaderCellDef> Name </th>
    <td mat-cell *matCellDef="let element"> {{element.DoctorName}} </td>
  </ng-container>
  <ng-container matColumnDef="Department">
    <th mat-header-cell *matHeaderCellDef> Department </th>
    <td mat-cell *matCellDef="let element"> {{element.Department}} </td>
  </ng-container>
  <ng-container matColumnDef="Specialization">
    <th mat-header-cell *matHeaderCellDef> Specialization </th>
    <td mat-cell *matCellDef="let element"> {{element.Specialization}} </td>
  </ng-container>
  <ng-container matColumnDef="GeneralVisit">
    <th mat-header-cell *matHeaderCellDef> General Visit </th>
    <td mat-cell *matCellDef="let element"> {{element.GeneralVisit}} </td>
  </ng-container>
  <ng-container matColumnDef="Action">
    <th mat-header-cell *matHeaderCellDef>Action </th>
    <td mat-cell *matCellDef="let element"> 
      <button class="btn" (click)="edit(element.name,element.id)">Edit</button> 
      <button class="btn" (click)="delete(element.name,element.id)">Delete</button> 
     </td>
  </ng-container>
  <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
  <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
</table>
</div>
