import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  public CustomControler: any;
  public Toggledata = true;

  form = new FormGroup({
    Email: new FormControl("", [Validators.required]),
    Password: new FormControl('', [Validators.required]),
  });

  get f() {
    return this.form.controls;
  }

logo: any;



ngOnInit() {
}

submit() {
  this.form.markAllAsTouched();
  //this.storage.Login(this.form.value);
  if (this.form.invalid) {
    return;
  }
  // this.spinner.showLoader();
  // this._authService.loginUser(this.form.value).subscribe((res: ResponseModel) => {
  //   console.log(res);
  //   if (!res.responseMsg.isError) {
  //     this.spinner.hideLoader();
  //     this._appService.setStorageData('_A', res.responseData.data);
  //     this._appService.setStorageData('_T', res._tokenData);
  //     this._appService.setStorageData('_R', res.responseData.role);

  //     this.router.navigate(['/layout/dashboard/admin']);
  //   } else {
  //     this.toastr.error("", "Invalid Email/Password, Please try again.");
  //   }
  // }, err => this.spinner.hideLoader())
}

ngOnDestroy() {
  //this.subscription.unsubscribe();
}

iconLogle() {
  this.Toggledata = !this.Toggledata
}

}
<div class="main-wrapper">

    <div class="account-content">
      <div class="container">
        <!-- Account Logo -->
        <div class="account-logo">
          <a href="javascript:"><img src="assets/img/EserveLogo/350w/logo-blue1.png"
              [alt]="logo" /></a>
        </div>
        <!-- /Account Logo -->

        <div class="account-box">
          <div class="account-wrapper">
            <h3 class="account-title">Login</h3>
            <p class="account-subtitle">Access to our dashboard</p>

            <!-- Account Form -->
            <form [formGroup]="form" (ngSubmit)="submit()">
              <div class="form-group">
                <label>Email Address</label>
                <input class="form-control" type="text" formControlName="Email" id="Email"
                  [ngClass]="{'is-invalid': f['Email'].touched && f['Email'].invalid}" />
                <div class="text-danger" *ngIf="CustomControler ?.status === 'Email'">
                  {{ CustomControler.message }}
                </div>
                <div *ngIf="f['Email'].touched && f['Email'].invalid" class="text-danger">
                  <div *ngIf="f['Email'].errors && f['Email'].errors['required']">Email is required.</div>
                  <div *ngIf="f['Email'].errors && f['Email'].errors['Email']">
                    Please, enter valid Email address.
                  </div>
                </div>
              </div>
              <div class="form-group">
                <div class="row">
                  <div class="col">
                    <label>Password</label>
                  </div>
                  <div class="col-auto">
                    <a class="text-muted" routerLink="/login/forgot">
                      Forgot Password?
                    </a>
                  </div>
                </div>
                <div class="position-relative">
                  <input class="form-control" [type]="Toggledata ? 'Password' : 'text'" formControlName="Password"
                    id="Password" [ngClass]="{'is-invalid': f['Password'].touched && f['Password'].invalid}" />
                  <span [ngClass]="Toggledata == true ? 'fa toggle-Password fa-eye' : 'fa toggle-Password fa-eye-slash'"
                    (click)="iconLogle()"></span>
                </div>
                <div *ngIf="f['Password'].touched && f['Password'].invalid" class="text-danger">
                  <div *ngIf="f['Password'].errors && f['Password'].errors['required']">
                    Password is required.
                  </div>
                </div>
                <div class="text-danger" *ngIf="CustomControler ?.status === 'Password'">{{ CustomControler.message }}</div>
              </div>
              <div class="form-group text-center">
                <button class="btn btn-primary account-btn" [disabled]="form.invalid" type="submit">Login</button>
              </div>
            </form>
            <!-- /Account Form -->
          </div>
        </div>
        <div class="company-logo">
          <p class="company-tag">Powered by <img src="assets/img/EserveLogo/350w/digi-surface-350-logo.png"
              [alt]="logo"  />
          </p>
        </div>
      </div>
    </div>
</div>
