import { Component, OnInit } from '@angular/core';
import {MatDialog} from '@angular/material/dialog';
import { AddServiceComponent } from './add-service/add-service.component';
import { DataService } from 'src/app/allService/data.service';
import { NgxSpinnerService } from "ngx-spinner";
import { MatTableDataSource } from '@angular/material/table';
import { PeriodicElement } from '../department/department.component';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-service',
  templateUrl: './service.component.html',
  styleUrls: ['./service.component.css']
})
export class ServiceComponent {
  constructor(public dialog: MatDialog,private spinner: NgxSpinnerService,private service:DataService) {
  }
  serviceGroup:any;
  displayedColumns: string[] = ['id', 'Service Name', 'Service Charge','Action'];
  listData!:PeriodicElement[];
  dataSource:any;

  ngOnInit(){
    this.spinner.show();
    this.service.getserviceGroup().subscribe((res:any)=>{
      this.serviceGroup=res.data
    })
    setTimeout(() => {
      this.spinner.hide();
    }, 1000);   
}
getSericeData(e:any){
this.service.getService(e.target.value).subscribe((res:any)=>{
  this.listData=res.data;
  this.dataSource=new MatTableDataSource(this.listData)
})
}
  openDialog(){
    const dialogRef = this.dialog.open(AddServiceComponent);
    dialogRef.afterClosed().subscribe(result => {
      this.ngOnInit()
    });
  }
  edit(name:any){
    const dialogRef = this.dialog.open(AddServiceComponent);
    dialogRef.componentInstance.dataSourse = name;
    dialogRef.afterClosed().subscribe(result => {
    this.ngOnInit()
    })
      }
      delete(name:any,id:any){
        Swal.fire({
          title: 'Are you sure?',
          text: "You won't be able to revert this!",
          icon: 'warning',
          showCancelButton: true,
          confirmButtonColor: '#3085d6',
          cancelButtonColor: '#d33',
          confirmButtonText: 'Yes, delete it!'
        }).then((result) => {
          if (result.isConfirmed) {
            Swal.fire(
              'Deleted!',
              'Your file has been deleted.',
              'success'
            )
          }
        })


      }

}
<button  (click)="openDialog()" class="btn btn-sm btn-primary">Add Service</button>
<!-- <ngx-spinner type="square-loader"></ngx-spinner> -->
<ngx-spinner bdColor = "rgba(0, 0, 0, .4)" size = "default" color = "black" type = "square-loader" [fullScreen] = "true"><p style="color: rgb(8, 1, 1)" >  </p></ngx-spinner>

<select class="form-select w-50 m-4" aria-label="Default select example" (change)="getSericeData($event)" >
  <option selected>Select Service</option>
  <option *ngFor="let s of serviceGroup"  >{{s.servicename}}</option>
</select>
<div class="m-4">
  <div class="table-heading">Services List</div>
<table mat-table [dataSource]="dataSource" class="mat-elevation-z8">
  <!-- Position Column -->
  <ng-container matColumnDef="id">
    <th mat-header-cell *matHeaderCellDef> Id. </th>
    <td mat-cell *matCellDef="let element"> {{element.id}} </td>
  </ng-container>

  <!-- Name Column -->
  <ng-container matColumnDef="Service Name">
    <th mat-header-cell *matHeaderCellDef> Service Name </th>
    <td mat-cell *matCellDef="let element"> {{element.sName}} </td>
  </ng-container>
  <ng-container matColumnDef="Service Charge">
    <th mat-header-cell *matHeaderCellDef> Service Charge </th>
    <td mat-cell *matCellDef="let element"> {{element.sCharge}} </td>
  </ng-container>
  <ng-container matColumnDef="Action">
    <th mat-header-cell *matHeaderCellDef>Action </th>
    <td mat-cell *matCellDef="let element"> 
      <button class="btn" (click)="edit(element)">Edit</button> 
      <button class="btn" (click)="delete(element.name,element.id)">Delete</button> 
     </td>
  </ng-container>

  <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
  <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
</table>
</div>
